mapper.select:

select * from (
select ec.id,ec.name as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,'text' as 'param' from tb_escalator_config ec where ec.ep_type='0' union
select ec.id,ep.param as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,ep.type as 'param' from tb_escalator_config ec left join tb_elev_param ep on ep.id=ec.id where ec.ep_type='1'
)e where e.type=#{type}

select * from (
select ec.id,ec.name as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,'text' as 'param' from tb_escalator_config ec where ec.ep_type='0' union
select ec.id,ep.param as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,ep.type as 'param' from tb_escalator_config ec left join tb_elev_param ep on ep.id=ec.id where ec.ep_type='1'
)e where e.parentId=#{id}

select * from tb_escalator_standard where id in(
	select es_id from tb_escalator_config where id=#{id}
)

mapper.update:
    UPDATE tb_e_offer
	SET
	stra_uuid                   =#{stra_uuid}
	Where
	stra_no=#{stra_no}

    UPDATE tb_e_offer
	SET
	instance_status    =#{instance_status}
	Where
	offer_id = #{offer_id}

mapper.select:

    select a.*,b.MODELS_NAME from tb_bjc a
	left join tb_models b on a.bjc_models=b.models_id
	where a.bjc_item_id=#{item_id}

